let navMenu=document.querySelector('nav');let mainContent=document.querySelector('main');let mainBtn=document.querySelector('.mainBtn');let navBtn=document.querySelector('.navBtn');let firstClick=!1;let isShown=!1;navigate=()=>{if(!firstClick){navMenu.classList.remove('initialHide');showMenu();firstClick=!0}else{isShown=!isShown;if(isShown)showMenu();else hideMenu()}}
showMenu=()=>{navMenu.classList.remove('hide');navMenu.classList.add('show');mainContent.classList.remove('show');mainContent.classList.add('hide')}
hideMenu=()=>{navMenu.classList.remove('show');navMenu.classList.add('hide');mainContent.classList.remove('hide');mainContent.classList.add('show')}
mainBtn.addEventListener('click',navigate);navBtn.addEventListener('click',navigate)